import React, { useState, useEffect } from "react";
import axios from "axios";
import "../CourseDetails/Course/Course.css";
function Othercon() {
  const [books, setBooks] = useState([]);
  const [showTable, setShowTable] = useState(false); // State to track if table should be shown

  useEffect(() => {
    // Fetch books from the backend API
    axios
      .get("http://localhost:8000/books")
      .then((response) => {
        setBooks(response.data);
      })
      .catch((error) => {
        console.error("Error fetching books:", error);
      });
  }, []);

  const handleToggleTable = () => {
    setShowTable((prevShowTable) => !prevShowTable);
    setShowImage((prevShowTable) => !prevShowTable);
  };

  return (
    <div>
      <h3 className="libary">School of Engineering Library</h3>

      <div className="para-and-table">
        <div className="para">
          School of Engineering (SOE) Library is one of the largest libraries
          that caters the information needs of students, teachers and research
          scholars belonging to the School. The library provides free and equal
          access to all sources of information. Open access is provided to
          ensure free searching, selection and use of library materials. Library
          tickets are issued to members for lending the books for outside use.
          The reading room of the library provides facilities for reference. The
          library is kept open for 9Â½ hours a day from 9 am to 6.30 pm without
          break. The library functions are managed by professionally competent
          staff members. The library keeps the resources in a secure environment
          for long use.
        </div>
        <div className="table">
          <h4 className="h4">Library Resources</h4>
          <p className="para" onClick={handleToggleTable}>
            Total Number of Books as on 31-03-2022
          </p>
          {showTable && (
            <table className="table-container">
              <tbody>
                {books.map((book) => (
                  <tr key={book.id}>
                    <td>{book.books}</td>
                    <td>{book.bookno}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
          {setShowImage && (
            <img
              src="https://fastly.4sqi.net/img/general/600x600/2716724_az0iMe2r-aGKNdzUKbvSnoedB93sSZTtr5aiFVohhsU.jpg"
              alt="soelib"
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default Othercon;
