// UpdateHostelPage.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";
import "./AdminDasbboard.css"

const UpdateHostelPage = () => {
  const { id } = useParams();
  const history = useHistory();
  const [courseId, setCourseId] = useState("");
  const [des, setDes] = useState("");
  const [des2, setDes2] = useState("");

  useEffect(() => {
    // Fetch the hostel data using the provided hostel ID
    axios
      .get(`http://localhost:8000/hostel/${id}`)
      .then((response) => {
        const { course_id, Des, des2 } = response.data;
        setCourseId(course_id);
        setDes(Des);
        setDes2(des2);
      })
      .catch((error) => {
        console.error("Error fetching hostel:", error);
      });
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit the updated hostel data
    const updatedHostel = {
      course_id: courseId,
      Des: des,
      des2: des2,
    };
    axios
      .put(`http://localhost:8000/hostel/${id}`, updatedHostel)
      .then((response) => {
        console.log("Hostel updated successfully");
        history.push("/Admin/dashboard"); // Redirect to the admin dashboard after successful update
      })
      .catch((error) => {
        console.error("Error updating hostel:", error);
      });
  };

  return (
    <div>
      <h1>Update Hostel</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>ID:</label>
          <input type="text" value={courseId} onChange={(e) => setCourseId(e.target.value)} />
        </div>
        <div>
          <label>Description:</label>
          <input type="text" value={des} onChange={(e) => setDes(e.target.value)} />
        </div>
        <div>
          <label>Description 2:</label>
          <input type="text" value={des2} onChange={(e) => setDes2(e.target.value)} />
        </div>
        <button type="submit">Update Hostel</button>
      </form>
    </div>
  );
};

export default UpdateHostelPage;
