import React, { useState, useEffect } from "react";
import "./Course.css";
import axios from "axios";

function Course() {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);

  useEffect(() => {
    // Fetch courses from the backend API
    axios
      .get("http://localhost:8000/courses")
      .then((response) => {
        setCourses(response.data);
      })
      .catch((error) => {
        console.error("Error fetching courses:", error);
      });
  }, []);

  const handleCourseClick = (course) => {
    setSelectedCourse(course);
  };

  return (
    <div className="course-container">
      <div className="header">
        <h2>COURSES</h2>
      </div>
      <div className="subheader">
        <h3>BTech Courses offered by Cochin University</h3>
      </div>
      {selectedCourse && (
        <div className="course-details">
          <img
            src={selectedCourse.photo_url}
            alt={selectedCourse.course_name}
            className="course-photo"
          />
          <div className="course-info">
            <h3 className="selected-course-name">
              {selectedCourse.course_name}
            </h3>
            <p className="selected-course-desc">{selectedCourse.course_desc}</p>
          </div>
        </div>
      )}
      <div>
        <ul className="course-list">
          {courses.map((course) => (
            <li
              key={course.course_id}
              onClick={() => handleCourseClick(course)}
            >
              <h4 className="course-name">{course.course_name}</h4>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Course;
