import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./adminDashboard.css";

class AdminDashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      courses: [],
      syllabus: [],
      books: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/login")
      .then((response) => {
        this.setState({ users: response.data });
      })
      .catch((error) => {
        console.log("Error fetching users:", error);
      });

    axios
      .get("http://localhost:8000/courses")
      .then((response) => {
        this.setState({ courses: response.data });
      })
      .catch((error) => {
        console.log("Error fetching courses:", error);
      });

    axios
      .get("http://localhost:8000/syllabus")
      .then((response) => {
        this.setState({ syllabus: response.data });
      })
      .catch((error) => {
        console.log("Error fetching syllabus:", error);
      });

    axios
      .get("http://localhost:8000/books")
      .then((response) => {
        this.setState({ books: response.data });
      })
      .catch((error) => {
        console.log("Error fetching books:", error);
      });
  }

  deleteUser = (userId) => {
    axios
      .delete(`http://localhost:8000/user/${userId}`)
      .then((response) => {
        // User deleted successfully
        console.log("User deleted successfully");
        // Update the users state by removing the deleted user
        this.setState((prevState) => ({
          users: prevState.users.filter((user) => user.id !== userId),
        }));
      })
      .catch((error) => {
        console.log("Error deleting user:", error);
      });
  };

  render() {
    const { users, courses, syllabus, books } = this.state;

    return (
      <div className="admin-dashboard-container">
        <h1 className="admin-dashboard-title">Admin Dashboard</h1>

        <h5 className="admin-dashboard-section-title">Users</h5>
        <table className="admin-dashboard-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button
                    onClick={() => this.deleteUser(user.id)}
                    className="delete-user-button"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Link to="/Adduserpage" className="admin-dashboard-link">
          Create New User
        </Link>

        <h5 className="admin-dashboard-section-title">Courses</h5>
        <table className="admin-dashboard-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Course Name</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {courses.map((course) => (
              <tr key={course.course_id}>
                <td>{course.course_id}</td>
                <td>{course.course_name}</td>
                <td>{course.course_desc}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <h5 className="admin-dashboard-section-title">Books</h5>
        <table className="admin-dashboard-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Book Name</th>
              <th>No of Books</th>
            </tr>
          </thead>
          <tbody>
            {books.map((book) => (
              <tr key={book.id}>
                <td>{book.id}</td>
                <td>{book.books}</td>
                <td>{book.bookno}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <h5 className="admin-dashboard-section-title">Syllabus</h5>
        <table className="admin-dashboard-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Syllabus</th>
            </tr>
          </thead>
          <tbody>
            {syllabus.map((item) => (
              <tr key={item.int}>
                <td>{item.int}</td>
                <td>{item.course_name}</td>
                <td>{item.syllabus}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Add sections for other functionalities (e.g., syllabus, books) */}
      </div>
    );
  }
}

export default AdminDashboard;
