import React from 'react';
import './Menu.css';
import img1 from './katha.jpg';
import img2 from './log.jpg';
import img3 from './tree.jpg';
import img4 from './gallery13 (1).jpg';
import img5 from './c-sis.jpg';


const Menu = () => {
  return (
    <div>
      <div className="heading">
        <h2>Cochin University of Science and Technology</h2>
        <div className="para">
          <p className="p">
            Cochin University of Science and Technology (CUSAT) was initially constituted as the University of Cochin through an Act of Kerala Government on 10th July 1971. The University of Cochin was re‐constituted as Cochin University of Science and Technology (CUSAT) in February 1986, redefining its objectives as "promoting Graduate and Post‐Graduate studies and Advanced Research in Applied Sciences, Technology, Industry, Commerce, Management and Social Sciences." CUSAT is now a world-ranking university with the specific purpose of developing higher education, emphasizing post-graduate studies and research in applied science, technology, industry, humanities, and commerce. CUSAT has consecutively been figured in the Times Higher Education World Ranking since 2017. The Times ranks around 1500 best universities worldwide annually with around 60 universities from India out of 967. CUSAT has also found a place in the QS World University Ranking and the National Institutional Ranking Framework (NIRF) of the India Government.
          </p>
        </div>
      </div>
      <Slideshow />
    </div>
  );
};

const Slideshow = () => {
  const handleRadioChange = (event) => {
    const slide = event.target.id;
    const slidesContainer = document.querySelector(".potter");

    // Calculate the index of the selected slide
    const selectedIndex = parseInt(slide.replace("img", "")) - 1;

    // Calculate the left margin to show the selected slide
    const leftMargin = selectedIndex * -20;

    slidesContainer.style.marginLeft = `${leftMargin}%`;
  };

  return (
    <div className="container">
      <div className="potter">
        <input type="radio" name="slide" id="img1" checked onChange={handleRadioChange} />
        <input type="radio" name="slide" id="img2" onChange={handleRadioChange} />
        <input type="radio" name="slide" id="img3" onChange={handleRadioChange} />
        <input type="radio" name="slide" id="img4" onChange={handleRadioChange} />
        <input type="radio" name="slide" id="img5" onChange={handleRadioChange} />

        <div className="slide first">
          <img src={img1} alt="img1" />
        </div>
        <div className="slide">
          <img src={img2} alt="img2" />
        </div>
        <div className="slide">
          <img src={img3} alt="img3" />
        </div>
        <div className="slide">
          <img src={img4} alt="img4" />
        </div>
        <div className="slide">
          <img src={img5} alt="img5" />
        </div>
      </div>

      <div className="navigation-man">
        <label htmlFor="img1" className="manual-b"></label>
        <label htmlFor="img2" className="manual-b"></label>
        <label htmlFor="img3" className="manual-b"></label>
        <label htmlFor="img4" className="manual-b"></label>
        <label htmlFor="img5" className="manual-b"></label>
      </div>
    </div>
  );
};


export default Menu;
