import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const UpdateHostelPage = () => {
  const { id } = useParams();
  const [hostelId, setHostelId] = useState("");
  const [des, setDes] = useState("");
  const [des2, setDes2] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch the hostel data using the provided hostel ID
    axios
      .get(`http://localhost:8000/hostel/${id}`)
      .then((response) => {
        const { id: hostelId, Des, des2 } = response.data;
        setHostelId(hostelId);
        setDes(Des);
        setDes2(des2);
        setLoading(false);
      })
      .catch((error) => {
        setError("Error fetching hostel data");
        setLoading(false);
      });
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Submit the updated hostel data
    const updatedHostel = {
      id: hostelId,
      Des: des,
      des2: des2,
    };
    try {
      await axios.put(`http://localhost:8000/hostel/${id}`, updatedHostel);
      console.log("Hostel updated successfully");
      // Redirect to the admin dashboard after successful update
      window.location.href = "/Admin/dashboard";
    } catch (error) {
      setError("Error updating hostel");
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Update Hostel</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="des">Des:</label>
          <input
            type="text"
            id="des"
            value={des}
            onChange={(e) => setDes(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="des2">Des2:</label>
          <input
            type="text"
            id="des2"
            value={des2}
            onChange={(e) => setDes2(e.target.value)}
          />
        </div>
        <button type="submit">Update Hostel</button>
      </form>
    </div>
  );
};

export default UpdateHostelPage;
