import React, { useState, useEffect } from "react";
import axios from "axios";

function Othercon() {
  const [books, setBooks] = useState([]);
  const [showTable, setShowTable] = useState(false); // State to track if table should be shown

  useEffect(() => {
    // Fetch books from the backend API
    axios
      .get("http://localhost:8000/books")
      .then((response) => {
        setBooks(response.data);
      })
      .catch((error) => {
        console.error("Error fetching books:", error);
      });
  }, []);

  const handleToggleTable = () => {
    setShowTable((prevShowTable) => !prevShowTable);
  };

  return (
    <div>
      <h3 className="libary">School of Engineering</h3>
      <h4 className="libary" onClick={handleToggleTable}>
        Library Resources
      </h4>
      {showTable && (
        <table className="table-container">
          <tbody>
            {books.map((book) => (
              <tr key={book.id}>
                <td>{book.books}</td>
                <td>{book.bookno}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Othercon;
