import React from 'react'

const Forgotten = () => {
    const [forgotEmail, setForgotEmail] = useState("");

    const handleForgotPassword = async (e) => {
        e.preventDefault();
        try {
          const res = await axios.post("http://localhost:8000/forgot-password", {
            email: forgotEmail,
          });
      
          console.log(res);
          setForgotEmail("");
          setErrorMessage("Password reset instructions sent to your email.");
        } catch (err) {
          console.log(err);
          setErrorMessage("Failed to reset password. Please try again.");
        }
      };
      
  return (
    <form>
  {errorMessage && <p className="error">{errorMessage}</p>}
  <label>Email</label>
  <input
    required
    type="email"
    name="email"
    placeholder="Enter your email"
    onChange={handleEmailChange}
  />
  <label>Password</label>
  <input
    required
    type="password"
    name="password"
    placeholder="Enter your password"
    onChange={handlePasswordChange}
  />
  <button className="but" onClick={handleLogin}>
    Login
  </button>
  <br />
  <br />
  <p className="register-link">
    Don't have an account? <Link to="/register">Register</Link>
  </p>
  <p className="forgot-password-link">
    Forgot your password? 
    <button className="forgot-password-button" onClick={handleForgotPassword}>
      Reset Password
    </button>
  </p>
</form>

  )
}

export default Forgotten
