import React, { useState } from "react";
import "./Eventscon.css";

const Eventscon = () => {
  const [selectedEvent, setSelectedEvent] = useState(null);

  const events = [
    {
      id: 1,
      name: "Event 1",
      description: "Description for Event 1",
      image: "https://soe.cusat.ac.in/images/demo/index.jpeg",
    },
    {
      id: 2,
      name: "Event 2",
      description: "Description for Event 2",
      image: "https://soe.cusat.ac.in/images/demo/about-us.png",
    },
    // Add more event objects as needed
  ];

  const handleEventClick = (event) => {
    setSelectedEvent(event);
  };

  return (
    <div className="event-container">
      <h2 className="event-heading">Events</h2>
      <div className="event-grid">
        {events.map((event) => (
          <div
            key={event.id}
            className="event-item"
            onClick={() => handleEventClick(event)}
          >
            <img
              src={event.image}
              alt={event.name}
              className={`event-image ${
                selectedEvent === event ? "hidden" : ""
              }`}
            />
            {selectedEvent === event && (
              <div className="event-description">
                <h3 className="event-name">{event.name}</h3>
                <p className="event-desc">{event.description}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Eventscon;
