import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./adminDashboard.css";

class AdminDashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      courses: [],
      syllabus: [],
      books: [],
    };
  }

  componentDidMount() {
    this.fetchUsers();
    this.fetchCourses();
    this.fetchSyllabus();
    this.fetchBooks();
  }

  fetchUsers() {
    axios
      .get("http://localhost:8000/users")
      .then((response) => {
        this.setState({ users: response.data });
      })
      .catch((error) => {
        console.log("Error fetching users:", error);
      });
  }

  fetchCourses() {
    axios
      .get("http://localhost:8000/courses")
      .then((response) => {
        this.setState({ courses: response.data });
      })
      .catch((error) => {
        console.log("Error fetching courses:", error);
      });
  }

  fetchSyllabus() {
    axios
      .get("http://localhost:8000/syllabus")
      .then((response) => {
        this.setState({ syllabus: response.data });
      })
      .catch((error) => {
        console.log("Error fetching syllabus:", error);
      });
  }

  fetchBooks() {
    axios
      .get("http://localhost:8000/books")
      .then((response) => {
        this.setState({ books: response.data });
      })
      .catch((error) => {
        console.log("Error fetching books:", error);
      });
  }

  handleUserRemoval(id) {
    axios
      .delete(`http://localhost:8000/users/${id}`)
      .then((response) => {
        console.log("User removed:", response.data);
        this.fetchUsers(); // Refresh the users list after removal
      })
      .catch((error) => {
        console.log("Error removing user:", error);
      });
  }

  render() {
    const { users, courses, syllabus, books } = this.state;

    return (
      <div className="admin-dashboard-container">
        <h1 className="admin-dashboard-title">Admin Dashboard</h1>

        <h5 className="admin-dashboard-section-title">Users</h5>
        <table className="admin-dashboard-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>
                  <button
                    className="admin-dashboard-remove-button"
                    onClick={() => this.handleUserRemoval(user.id)}
                  >
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Link to="/Adduserpage" className="admin-dashboard-link">
          Create New User
        </Link>

        <h5 className="admin-dashboard-section-title">Courses</h5>
        {/* Render courses table */}

        <h5 className="admin-dashboard-section-title">Books</h5>
        {/* Render books table */}

        <h5 className="admin-dashboard-section-title">Syllabus</h5>
        {/* Render syllabus table */}
      </div>
    );
  }
}

export default AdminDashboard;
