import React from "react";
import axios from "axios";

class AdminDashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      courses: [],
      syllabus: [],
      books: [],
      showUsersTable: false,
      showCoursesTable: false,
      showBooksTable: false,
      showSyllabusTable: false,
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/users")
      .then((response) => {
        this.setState({ users: response.data });
      })
      .catch((error) => {
        console.log("Error fetching users:", error);
      });

    axios
      .get("http://localhost:8000/syllabus")
      .then((response) => {
        this.setState({ courses: response.data });
      })
      .catch((error) => {
        console.log("Error fetching courses:", error);
      });
    axios
      .get("http://localhost:8000/books")
      .then((response) => {
        this.setState({ courses: response.data });
      })
      .catch((error) => {
        console.log("Error fetching courses:", error);
      });

    // Fetch data for other functionalities (e.g., syllabus, books)
  }

  toggleTable = (table) => {
    this.setState((prevState) => ({
      [table]: !prevState[table],
    }));
  };

  render() {
    const {
      users,
      courses,
      syllabus,
      books,
      showUsersTable,
      showCoursesTable,
      showBooksTable,
      showSyllabusTable,
    } = this.state;

    return (
      <div>
        <h1>Admin Dashboard</h1>

        <h2 onClick={() => this.toggleTable("showUsersTable")}>Users</h2>
        {showUsersTable && (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        <h2 onClick={() => this.toggleTable("showCoursesTable")}>Courses</h2>
        {showCoursesTable && (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Course Name</th>
                <th>Description</th>
              </tr>
            </thead>
            <tbody>
              {courses.map((course) => (
                <tr key={course.id}>
                  <td>{course.id}</td>
                  <td>{course.courseName}</td>
                  <td>{course.description}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        <h2 onClick={() => this.toggleTable("showBooksTable")}>Books</h2>
        {showBooksTable && (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Book Name</th>
                <th>No of Books</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.id}>
                  <td>{book.id}</td>
                  <td>{book.bookName}</td>
                  <td>{book.noOfBooks}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        <h2 onClick={() => this.toggleTable("showSyllabusTable")}>Syllabus</h2>
        {showSyllabusTable && (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Syllabus</th>
              </tr>
            </thead>
            <tbody>
              {syllabus.map((item) => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.syllabus}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {/* Add sections for other functionalities (e.g., syllabus, books) */}
      </div>
    );
  }
}

export default AdminDashboard;
