import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const UpdateHostelPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [hostel, setHostel] = useState(null); // Initialize as null
  const [Des, setDes] = useState("");
  const [des2, setDes2] = useState("");

  useEffect(() => {
    // Fetch the hostel data using the provided hostel ID
    axios
      .get(`http://localhost:8000/hostel/${id}`)
      .then((response) => {
        setHostel(response.data);
        setDes(response.data.Des);
        setDes2(response.data.des2);
      })
      .catch((error) => {
        console.error("Error fetching hostel:", error);
      });
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Submit the updated hostel data
    const updatedHostel = {
      Des: Des,
      des2: des2,
    };
    try {
      await axios.put(`http://localhost:8000/hostel/${id}`, updatedHostel);
      console.log("Hostel updated successfully");
      // Navigate to the main page after successful update
      navigate("/");
    } catch (error) {
      console.error("Error updating hostel:", error);
    }
  };

  if (hostel === null) {
    return <div>Loading...</div>; // Add a loading state while fetching the data
  }

  return (
    <div>
      <h1>Update Hostel</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="Des">Description:</label>
          <input
            type="text"
            id="Des"
            value={Des}
            onChange={(e) => setDes(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="des2">Description 2:</label>
          <input
            type="text"
            id="des2"
            value={des2}
            onChange={(e) => setDes2(e.target.value)}
          />
        </div>
        <button type="submit">Update Hostel</button>
      </form>
    </div>
  );
};

export default UpdateHostelPage;
