import React, { useState, useEffect } from "react";
import "./Course.css";
import axios from "axios";

function Course() {
  const [courses, setCourses] = useState([]);
  const [syllabus, setSyllabus] = useState([]);

  useEffect(() => {
    // Fetch courses from the backend API
    axios
      .get("http://localhost:8000/courses")
      .then((response) => {
        setCourses(response.data);
      })
      .catch((error) => {
        console.error("Error fetching courses:", error);
      });
  }, []);

  useEffect(() => {
    // Fetch syllabus from the backend API
    axios
      .get("http://localhost:8000/syllabus")
      .then((response) => {
        setSyllabus(response.data);
      })
      .catch((error) => {
        console.error("Error fetching syllabus:", error);
      });
  }, []);

  return (
    <div className="course-container">
      <div className="courses">
        <h2>COURSES</h2>
      </div>
      <div className="subheader">
        <h3>BTech Courses offered by Cochin University</h3>
      </div>
      <div className="photo-and-course">
        <div className="course-photo-container">
          <img
            src="https://soe.cusat.ac.in/images/demo/office.png" // Replace with the actual image URL
            alt="officePhoto"
            className="course-photo"
          />
        </div>
        <ul className="course-list">
          {courses.map((course) => (
            <li key={course.id}>
              <h4 className="course-name">{course.course_name}</h4>
            </li>
          ))}
        </ul>
      </div>
      <div className="syllabus-table">
        <h3>Syllabus</h3>
        <table>
          <thead>
            <tr>
              <th>Course Name</th>
              <th>Syllabus Link</th>
            </tr>
          </thead>
          <tbody>
            {syllabus.map((syllabusItem) => (
              <tr key={syllabusItem.id}>
                <td>{syllabusItem.course_name}</td>
                <td>{syllabusItem.syllabus_link}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Course;
