import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const UpdateHostelPage = () => {
  const { id } = useParams();
  const [hostelId, setHostelId] = useState(""); // Rename id to hostelId
  const [des, setDes] = useState("");
  const [des2, setDes2] = useState("");

  useEffect(() => {
    // Fetch the hostel data using the provided hostel ID
    axios
      .get(`http://localhost:8000/hostel/${id}`)
      .then((response) => {
        const { id: hostelId, Des, des2 } = response.data; // Update id to hostelId
        setHostelId(hostelId);
        setDes(Des);
        setDes2(des2);
      })
      .catch((error) => {
        console.error("Error fetching hostel:", error);
      });
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Submit the updated hostel data
    const updatedHostel = {
      id: hostelId, // Update id to hostelId
      Des: des,
      des2: des2,
    };
    try {
      await axios.put(`http://localhost:8000/hostel/${id}`, updatedHostel);
      console.log("Hostel updated successfully");
      // Redirect to the admin dashboard after successful update
      window.location.href = "/Admin/dashboard";
    } catch (error) {
      console.error("Error updating hostel:", error);
    }
  };

  return (
    <div>
      <h1>Update Hostel</h1>
      <form onSubmit={handleSubmit}>
        {/* Form input fields */}
        <button type="submit">Update Hostel</button>
      </form>
    </div>
  );
};

export default UpdateHostelPage;
