import React, { useState, useEffect } from "react";
import "./Course.css";
import axios from "axios";

function Course() {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);

  const [syllabus, setSyllabus] = useState([]);
  const [selectedSyllabus, setSelectedSyllabus] = useState(null);

  useEffect(() => {
    // Fetch courses from the backend API
    axios
      .get("http://localhost:8000/courses")
      .then((response) => {
        setCourses(response.data);
      })
      .catch((error) => {
        console.error("Error fetching courses:", error);
      });
  }, []);

  const handleCourseClick = (course) => {
    setSelectedCourse(course);
  };

  useEffect(() => {
    // Fetch syllabus from the backend API
    axios
      .get("http://localhost:8000/syllabus")
      .then((response) => {
        setSyllabus(response.data);
      })
      .catch((error) => {
        console.error("Error fetching syllabus:", error);
      });
  }, []);

  const handleSyllabusClick = (syllabus) => {
    setSelectedSyllabus(syllabus);
  };

  return (
    <div className="course-container">
      <div className="courses">
        <h2>COURSES</h2>
      </div>
      <div className="subheader">
        <h3>BTech Courses offered by Cochin University</h3>
      </div>
      <div className="photo-and-course">
        <div className="course-photo-container">
          <img
            src="https://soe.cusat.ac.in/images/demo/office.png" // Replace with the actual image URL
            alt="officePhoto"
            className="course-photo"
          />
        </div>
        <ul className="course-list">
          {courses.map((course) => (
            <li key={course.id} onClick={() => handleCourseClick(course)}>
              <h4 className="course-name">{course.course_name}</h4>
            </li>
          ))}
        </ul>
      </div>
      {selectedCourse && (
        <div>
          <h3 className="selected-course-name">{selectedCourse.course_name}</h3>
          <p className="selected-course-desc">{selectedCourse.course_desc}</p>
        </div>
      )}
      <div className="syllabus-table">
        <h3>Syllabus</h3>
        <table>
          <thead>
            <tr>
              <th>Course Name</th>
              <th>Syllabus Link</th>
            </tr>
          </thead>
          <tbody>
            {syllabus.map((syllabusItem) => (
              <tr
                key={syllabusItem.id}
                onClick={() => handleSyllabusClick(syllabusItem)}
              >
                <td>{syllabusItem.course_name}</td>
                <td>{syllabusItem.syllabus_link}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {selectedSyllabus && (
        <div>
          <h3>Selected Syllabus</h3>
          <p>Course Name: {selectedSyllabus.course_name}</p>
          <p>Syllabus Link: {selectedSyllabus.syllabus_link}</p>
        </div>
      )}
    </div>
  );
}

export default Course;
