import React, { useState } from "react";
import "./Eventscon.css";

const Eventscon = () => {
  const [activeItem, setActiveItem] = useState(null);

  const handleEventClick = (event) => {
    setActiveItem(event.id);
  };

  const events = [
    {
      id: 1,
      name: "Event 1",
      description: "Description for Event 1",
      image: "path/to/event1-image.jpg",
    },
    {
      id: 2,
      name: "Event 2",
      description: "Description for Event 2",
      image: "path/to/event2-image.jpg",
    },
    {
      id: 3,
      name: "Event 3",
      description: "Description for Event 3",
      image: "https://soe.cusat.ac.in/images/demo/index.jpeg",
    },
  ];

  return (
    <div id="grid">
      {events.map((event) => (
        <div
          key={event.id}
          className={`event ${activeItem === event.id ? "active" : ""}`}
          onClick={() => handleEventClick(event)}
        >
          <img src={event.image} alt={event.name} className="event-image" />
          <div className="event-details">
            <h3 className="event-name">{event.name}</h3>
            <p className="event-description">{event.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Eventscon;
